mapper.update:
update user_tb
   set user_nickname = #{userNickname},
       user_name = #{userName},
       category_list = #{categoryList}
 where user_no = #{userNo}

mapper.select:
select count(*)
  from subscribe_tb
 where sub_user_no = #{subUserNo}
   and opp_user_no = #{oppUserNo}

mapper.delete:
delete from subscribe_tb
 where sub_user_no = #{subUserNo}
   and opp_user_no = #{oppUserNo}
